import React, { Component } from 'react';

import '../styles/NotePage.css'

class NotePage extends Component {
  constructor(){
    super()

    this.state={
      noteTitle:""
    }
  }
 
  handleInputChange = (event) => {
    this.setState({noteTitle : event.target.value})
  }

  canBeSubmitted(){
    const { noteTitle } = this.state
    console.log(noteTitle.length)
    return noteTitle.length > 0
  }

  render(){
    return(
      <div className="">

        <form action="/api/notes" method="post">

          <div className="row">
            <div className="col">
              <input type="text" placeholder="title" name="title" onChange={this.handleInputChange} />
            </div>
          </div>
          
          <div className="row">
            <div className="col note-holder">
              <textarea type="text" placeholder="note" name="note" rows="8"/>
            </div>
          </div>

          <div className="row">
            <div className="col">
              <input type="submit" value="Submit" disabled={this.canBeSubmitted}/>
            </div>
          </div>
          
        </form>

      </div>
    )
  }

}

export default NotePage
