import React, { Component } from 'react';

import '../styles/NotePage.css'

class NotePage extends Component {
  constructor(){
    super()

    this.state={
      titleRef : ""
    }
  }

  render(){
    return(
      <div className="">

        <form action="/api/notes" method="post">

          <div className="row">
            <div className="col">
              <input type="text" placeholder="title" name="title" ref={input => this.titleRef = input} />
            </div>
          </div>
          
          <div className="row">
            <div className="col note-holder">
              <textarea type="text" placeholder="note" name="note" rows="8"/>
            </div>
          </div>

          <div className="row">
            <div className="col">
              <input type="submit" value="Submit" disabled={true}/>
            </div>
          </div>
          
        </form>

      </div>
    )
  }

}

export default NotePage
